%cassandra
CREATE KEYSPACE IF NOT EXISTS weatherdata WITH replication = {'class': 'SimpleStrategy', 'replication_factor': 1 };

USE weatherdata;

CREATE TABLE IF NOT EXISTS  temperature (
weatherstation_id text,
event_time timestamp,
temperature text,
PRIMARY KEY (weatherstation_id,event_time)
);

